mapper.select:
select * from sudi_view_foodSpu

     and foodId=#{foodId} 
     and foodName like concat('%', #{foodName}, '%') 
     and typeId=#{typeId} 
     and recommend=#{recommend} 
     and hotSale=#{hotSale} 
     and foodStatus=#{foodStatus}

mapper.update:
update sudi_food
foodName=#{foodName}, foodVegon=#{foodVegon}, foodtypeId=#{typeId}, foodCookWay=#{foodCookWay}, foodIngredient=#{foodIngredient}, foodImage=#{foodImage}, recommend=#{recommend}, hotSale=#{hotSale}, foodDesc=#{foodDesc}, lastModifyBy=#{lastModifyBy}, lastModifyTime=NOW(),
where foodId=#{foodId}
